urlset.script:
const xhr = new XMLHttpRequest();
    xhr.open('GET', 'https://sebdoe-api.herokuapp.com/sitemap.xml', true);

    xhr.responseType = 'document';

    xhr.onload = () => {
        document.getElementsByTagName('urlset')[0].innerHTML = xhr.responseXML.childNodes[0].innerHTML;
    }

    xhr.onerror = () => {
        document.getElementsByTagName('urlset')[0].innerHTML = '<url><loc>https://sebdoe.com</loc></url>';
    }

    xhr.send();
